import { useState, useEffect } from "react";

const InvoicingDetail = () => {
  const [invoicingData, setInvoicingData] = useState([]);
  const [error, setError] = useState("");

const numberFormatter = new Intl.NumberFormat("en-US", {
  minimumFractionDigits: 0,
  maximumFractionDigits: 0,
});

const formatNumber = (value) =>
  typeof value === "number" ? numberFormatter.format(value) : "-";

const twoDecimalFormatter = new Intl.NumberFormat("en-US", {
  minimumFractionDigits: 2,
  maximumFractionDigits: 2,
});

useEffect(() => {
  fetch("http://10.13.10.12:5001/api/invoicing")
    .then((res) => res.json())
    .then((data) => {
      console.log("📥 Received from API:", data);
      setInvoicingData(data);
    })
    .catch((err) => {
      console.error("❌ Fetch error:", err);
      setError("Error fetching data");
    });
}, []);

  return (
    <div>
      <h1>InvoicingDetail</h1>
      {/* Detail table */}
      <div className="invoicingData-list">
        {error && <div className="error-message">{error}</div>}
        <div className="table-container">
          <table>
            <thead>
              <tr>
                <th>source</th>
                <th>empresa</th>
                <th>documento</th>
                <th>dt_documento</th>
                <th>item</th>
                <th>status</th>
                <th>cliente</th>
                <th>container_id</th>
                <th className="number-cell">qty</th>
                <th className="number-cell">fob_usd</th>
                <th className="number-cell">flete_usd</th>            
              </tr>
            </thead>
            <tbody>
              {invoicingData
                .sort((a, b) => a.dt_documento.localeCompare(b.dt_documento)) // ascending
                .map((invoice) => (
                  <tr key={invoice}>
                    <td>{invoice.source}</td>
                    <td>{invoice.empresa}</td>
                    <td>{invoice.documento}</td>
                    <td>{invoice.dt_documento}</td>
                    <td>{invoice.item}</td>
                    <td>{invoice.status}</td>
                    <td>{invoice.cliente}</td>
                    <td>{invoice.container_id}</td>
                    <td className="number-cell">{twoDecimalFormatter.format(invoice.qty)}</td>
                    <td className="number-cell">{twoDecimalFormatter.format(invoice.fob_usd)}</td>
                    <td className="number-cell">{twoDecimalFormatter.format(invoice.flete_usd)}</td>
                  </tr>
                ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default InvoicingDetail;
