// Navbar.jsx
import { Link, useNavigate } from "react-router-dom";

const Navbar = () => {
  const token = localStorage.getItem("token");
  const role = localStorage.getItem("role"); // ðŸ‘ˆ store this when user signs in
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("role"); // clear role too
    navigate("/signIn");
  };

  return (
    <nav className="navbar">
      <div className="navbar-container">
        <Link to="/" className="nav-brand">
          Plantly BI
        </Link>

        <div className="nav-links">
          {/* Example permissions */}
          {token && role === "admin" && (
            <Link to="/data-sources" className="nav-link">
              Data Sources
            </Link>
          )}

          {token && ["admin", "manager"].includes(role) && (
            <Link to="/rlsDM" className="nav-link">
              RLS DM
            </Link>
          )}

          {!token ? (
            <>
              <Link to="/signIn" className="nav-link">
                Sign In
              </Link>
              <Link to="/signup" className="nav-link">
                Sign Up
              </Link>
            </>
          ) : (
            <button onClick={handleLogout} className="nav-link btn-logout">
              Logout
            </button>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
