import React, { useState, useEffect } from "react";
import axios from "axios";

const Dashboard = () => {
  const [kpis, setKpis] = useState([]);
  const [error, setError] = useState("");
  const [kpiSearchTerm, setKpiSearchTerm] = useState("");
  const [statusSearchTerm, setStatusSearchTerm] = useState("");

  useEffect(() => {
    fetchKpis();
  }, []);

  const fetchKpis = async () => {
    try {
      const response = await axios.get("http://localhost:5002/api/kpis");
      setKpis(response.data);
    } catch (error) {
      console.error("Error fetching data:", error);
      setError("Failed to load data. Please try again later.");
    }
  };

  return (
    <div>
      <h1>Dashboard</h1>

      {/* KPIs */}
      <div className="kpis">
        {/* {kpis.map((kpi) => (
          <div className="kpi" key={kpi._id}>
            <h1>{kpi.value}</h1>
            <p>{kpi.name}</p>
          </div>
        ))} */}
        <div className="kpi">
          <h1>2,000</h1>
          <p>Exported (FCL)</p>
        </div>
        <div className="kpi">
          <h1>60,000,000</h1>
          <p>Production Cost (USD)</p>
        </div>
        <div className="kpi">
          <h1>80,000,000</h1>
          <p>FOB Sales (USD)</p>
        </div>
        <div className="kpi">
          <h1>10,000</h1>
          <p>Avg Margin per FCL</p>
        </div>
      </div>

      {/* Summary table */}
      <div className="kpis-list">
        <h3>Seasons</h3>

        {error && <div className="error-message">{error}</div>}
        <div className="table-container">
          <table>
            <thead>
              <tr>
                {/* <th>
                  <div className="table-header">
                    Season
                    <input
                      type="text"
                      placeholder="Search by Season"
                      value={kpiSearchTerm}
                      onChange={(e) => setKpiSearchTerm(e.target.value)}
                      className="search-input"
                    />
                  </div>
                </th> */}
                <th>Season</th>
                <th>Crop</th>
                <th>Exported (FCL)</th>
                <th>Exported (MT)</th>
                <th>Prod.Cost (USD)</th>
                <th>FOB.Sales (USD)</th>
                <th>Margin (USD)</th>
                {/* <th>
                  <div className="table-header">
                    Status
                    <input
                      type="text"
                      placeholder="Search by Status"
                      value={statusSearchTerm}
                      onChange={(e) => setStatusSearchTerm(e.target.value)}
                      className="search-input"
                    />
                  </div>
                </th> */}
              </tr>
            </thead>
            <tbody>
              {kpis
                .filter(
                  (kpi) =>
                    kpi.name
                      .toLowerCase()
                      .includes(kpiSearchTerm.toLowerCase()) 
                    // &&
                    // kpi.value
                    //   .toLowerCase()
                    //   .includes(statusSearchTerm.toLowerCase())
                )
                .map((kpi) => (
                  <tr key={kpi._id}>
                    <td>{kpi.name}</td>
                    <td>{kpi.value}</td>
                    <td>{kpi.status}</td>
                    <td>{kpi.kpiName}</td>
                  </tr>
                ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
