import { useState, useEffect } from "react";
import axios from "axios";
import Modal from "react-modal";

// required for accessibility
Modal.setAppElement("#root");

const DataSourcesManager = () => {
  const [dataSources, setDataSources] = useState([]);
  const [newDataSource, setNewDataSource] = useState({
    source_id: "",
    source_system: "",
    source_location: "",
    host_api_url: "",
    source_type: "",
    system_type: "",
    source_name: "",
    source_status: "",
    valid_from: "",
    valid_to: "",
    source_description: "",
  });
  const [editingDataSource, setEditingDataSource] = useState(null);
  const [error, setError] = useState("");
  const [isAddModalOpen, setIsAddModalOpen] = useState(false);

  // 🔹 modal handlers
  const openAddModal = () => setIsAddModalOpen(true);
  const closeAddModal = () => setIsAddModalOpen(false);
  const closeEditModal = () => setEditingDataSource(null);

  useEffect(() => {
    fetchDataSources();
  }, []);

  const fetchDataSources = async () => {
    try {
      const response = await axios.get("http://10.13.10.12:5001/api/dataSources");
      setDataSources(response.data);
    } catch (error) {
      console.error("Error fetching data sources:", error);
      setError("Failed to load data sources");
    }
  };

  const handleInputChange = (e, isEditing = false) => {
    const { name, value } = e.target;
    if (isEditing) {
      setEditingDataSource((prev) => ({ ...prev, [name]: value }));
    } else {
      setNewDataSource((prev) => ({ ...prev, [name]: value }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://10.13.10.12:5001/api/dataSources", newDataSource);
      setNewDataSource({
        source_id: "",
        source_system: "",
        source_location: "",
        host_api_url: "",
        source_type: "",
        system_type: "",
        source_name: "",
        source_status: "",
        valid_from: "",
        valid_to: "",
        source_description: "",
      });
      fetchDataSources();
      setError("");
      closeAddModal();
    } catch (error) {
      console.error("Error creating data source:", error);
      const errorMessage =
        error.response?.data?.message || "Failed to create data source";
      setError(errorMessage);
    }
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      await axios.patch(
        `http://10.13.10.12:5001/api/dataSources/${editingDataSource.source_id}`,
        editingDataSource
      );
      closeEditModal();
      fetchDataSources();
      setError("");
    } catch (error) {
      console.error("Error updating data source:", error);
      setError("Failed to update data source");
    }
  };

  // 🔹 Reusable modal style
const modalStyle = {
  overlay: {
    backgroundColor: "rgba(0, 0, 0, 0.6)", // dark semi-transparent background
    zIndex: 1000, // make sure it's on top of everything
  },
  content: {
    width: "400px",
    height: "500px",
    margin: "auto",
    padding: "20px",
    borderRadius: "10px",
    zIndex: 1001, // higher than overlay
  },
};

  return (
    <div className="data-source-manager">
      <h2>Manage Data Sources</h2>

      {/* Data Source List */}
      <div className="data-source-list">
        <h3>Current Data Sources</h3>
        {error && <div className="error-message">{error}</div>}
        <div className="table-container">
          <table>
            <thead>
              <tr>
                <th>Source ID</th>
                <th>Source System</th>
                <th>Source Location</th>
                <th>Host/API URL</th>
                <th>Source Type</th>
                <th>System Type</th>
                <th>Source Name</th>
                <th>Source Status</th>
                <th>Valid From</th>
                <th>Valid To</th>
                <th>Edit</th>
              </tr>
            </thead>
            <tbody>
              {dataSources.map((dataSource) => (
                <tr key={dataSource.source_id}>
                  <td>{dataSource.source_id}</td>
                  <td>{dataSource.source_system}</td>
                  <td>{dataSource.source_location}</td>
                  <td>{dataSource.host_api_url}</td>
                  <td>{dataSource.source_type}</td>
                  <td>{dataSource.system_type}</td>
                  <td>{dataSource.source_name}</td>
                  <td>{dataSource.source_status}</td>
                  <td>{dataSource.valid_from}</td>
                  <td>{dataSource.valid_to}</td>
                  <td>
                    <button
                      onClick={() => setEditingDataSource(dataSource)}
                      className="btn btn-small btn-edit"
                    >
                      Edit
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {/* ➕ Add Modal */}
      <Modal isOpen={isAddModalOpen} onRequestClose={closeAddModal} style={modalStyle}>
        <h2>Add New Data Source</h2>
        <form onSubmit={handleSubmit}>
          {Object.keys(newDataSource).map((field) => (
            <div className="form-group" key={field}>
              <input
                type={
                  field.includes("date") ? "date" : field === "source_id" ? "number" : "text"
                }
                name={field}
                value={newDataSource[field]}
                onChange={(e) => handleInputChange(e, false)}
                placeholder={field.replace(/_/g, " ")}
                className="form-input"
                required={field !== "valid_from" && field !== "valid_to"} // dates optional
              />
            </div>
          ))}
          <button type="submit" className="btn">
            Add Data Source
          </button>
        </form>
      </Modal>

      {/* ✏️ Edit Modal */}
      <Modal isOpen={!!editingDataSource} onRequestClose={closeEditModal} style={modalStyle}>
        <h3>Edit Data Source</h3>
        {editingDataSource && (
          <form onSubmit={handleUpdate}>
            {Object.keys(editingDataSource).map((field) => (
              <div className="form-group" key={field}>
                <input
                  type={
                    field.includes("date")
                      ? "date"
                      : field === "source_id"
                      ? "number"
                      : "text"
                  }
                  name={field}
                  value={editingDataSource[field]}
                  onChange={(e) => handleInputChange(e, true)}
                  placeholder={field.replace(/_/g, " ")}
                  className="form-input"
                  disabled={field === "source_id"} // don't edit ID
                  required={
                    field !== "valid_from" && field !== "valid_to" && field !== "source_description"
                  }
                />
              </div>
            ))}
            <div className="modal-actions">
              <button type="submit" className="btn">
                Save Changes
              </button>
              <button type="button" onClick={closeEditModal} className="btn btn-cancel">
                Cancel
              </button>
            </div>
          </form>
        )}
      </Modal>

      <br />
      <button onClick={openAddModal} className="btn">
        Add New Data Source
      </button>
    </div>
  );
};

export default DataSourcesManager;
